using Amozon.Core.Entites.Order_Aggregate;using Microsoft.EntityFrameworkCore;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;namespace Amazon.Repository.Data.Config{    public class DeliveryMethodConfiguration : IEntityTypeConfiguration<DeliveryMethod>    {        public void Configure(Microsoft.EntityFrameworkCore.Metadata.Builders.EntityTypeBuilder<DeliveryMethod> builder)        {           builder.Property(deliveymethod=>deliveymethod.Cost).HasColumnType("Decimal(18,2)");        }    }}